    /// @dev Will revert if the entry with the given nonce does not exist.
    /// @param writer       The address of the writer on this chain
    /// @param writerNonce  The nonce of the writer's entry on this chain
    function getEntry(address writer, uint256 writerNonce) external view returns (InterchainEntry memory);

    /// @notice Get the nonce of the writer on this chain.
    /// @param writer       The address of the writer on this chain
    function getWriterNonce(address writer) external view returns (uint256);

    /// @notice Read the data written on specific source chain by a specific writer,
    /// and verify it on the destination chain using the provided Interchain Module.
    /// Note: returned zero value indicates that the module has not verified the entry.
    /// @param entry        The Interchain Entry to read
    /// @param dstModule    The destination chain addresses of the Interchain Modules to use for verification
    /// @return moduleVerifiedAt   The block timestamp at which the entry was verified by the module,
    ///                             or zero if the module has not verified the entry.
    function readEntry(
        address dstModule,
        InterchainEntry memory entry
    )
        external
        view
        returns (uint256 moduleVerifiedAt);
}

/// @notice Every Module may opt a different method to confirm the verified entries on destination chain,
/// therefore this is not a part of a common interface.
interface IInterchainModule {
    /// @notice Request the verification of an entry in the Interchain DataBase by the module.
    /// Note: a fee is paid to the module for verification, and could be retrieved by using `getModuleFee`.
    /// Note: this will eventually trigger `InterchainDB.verifyEntry(entry)` function on destination chain,
    /// with no guarantee of ordering.
    /// @dev Could be only called by the Interchain DataBase contract.
    /// @param destChainId  The chain id of the destination chain
    /// @param entry        The entry to verify
    function requestVerification(uint256 destChainId, InterchainEntry memory entry) external payable;

    /// @notice Get the Module fee for verifying an entry on the specified destination chain
    /// @param destChainId  The chain id of the destination chain
    function getModuleFee(uint256 destChainId) external view returns (uint256);
}

interface ISynapseModuleEvents {
  event VerfificationRequested(
    uint256 indexed destChainId,
    InterchainEntry entry
  );
  event EntryVerified(InterchainEntry entry);
}

contract SynapseModule is Ownable, SynapseGasService, ISynapseModuleEvents {
    address[] public verifiers;
    uint256 public requiredThreshold;
    address public interchainDB;

    constructor() public Ownable(msg.sender) {}

    function setInterchainDB(address _interchainDB) public onlyOwner {
        interchainDB = _interchainDB;
    }

    function setRequiredThreshold(uint256 _threshold) public onlyOwner {
        requiredThreshold = _threshold;
    }

    function setVerifiers(address[] calldata _verifiers) public onlyOwner {
        verifiers = _verifiers;
    }

    function requestVerification(
        uint256 destChainId,
        InterchainEntry memory entry
    ) external payable {
        require(
            msg.sender == interchainDB,
            'Only InterchainDB can request verification'
        );

        require(
            msg.value >= getModuleFee(destChainId),
            'Insufficient fee to request verification'
        );

        _payFeesForExecution(msg.value);
        emit VerfificationRequested(destChainId, entry);
    }

    function verifyEntry(
        InterchainEntry memory entry,
        bytes[] calldata signatures
    ) external {
        bytes32 messageHashToCheck = keccak256(abi.encode(entry));

        require(
            signatures.length >= requiredThreshold,
            'Not enough signatures to meet the threshold'
        );

        uint256 validSignatures;
        for (uint256 i = 0; i < verifiers.length; i++) {
            // TODO: Use TryRecover for explicit error handling
            address signer = ECDSA.recover(messageHashToCheck, signatures[i]);
            for (uint256 j = 0; j < verifiers.length; j++) {
                if (signer == verifiers[j]) {
                    validSignatures++;
                    break;
                }
            }
        }

        require(
            validSignatures >= requiredThreshold,
            'Not enough valid signatures to meet the threshold'
        );

        IInterchainDB(interchainDB).verifyEntry(entry);

        emit EntryVerified(entry);
    }
}

