pragma solidity =0.8.20 ^0.8.0 ^0.8.20;

// contracts/interfaces/IInterchainApp.sol

interface IInterchainApp {
    function setAppConfig(
        uint64[] memory chainIDs,
        address[] memory linkedIApps,
        address[] memory sendingModules,
        address[] memory receivingModules,
        uint256 requiredResponses,
        uint64 optimisticTimePeriod
    )
        external;

    function getLinkedIApp(uint64 chainID) external view returns (address);

    function getSendingModules() external view returns (address[] memory);

    function getReceivingModules() external view returns (address[] memory);

    function getRequiredResponses() external view returns (uint256);

    function getOptimisticTimePeriod() external view returns (uint64);

    function send(bytes32 receiver, uint256 dstChainId, bytes calldata message) external payable;

    function appReceive() external;
}

// contracts/interfaces/IInterchainClientV1.sol

interface IInterchainClientV1 {
    /**
     * @notice Sets the linked client for a specific chain ID.
     * @dev Stores the address of the linked client in a mapping with the chain ID as the key.
     * @param chainId The chain ID for which the client is being set.
     * @param client The address of the client being linked.
     */
    function setLinkedClient(uint256 chainId, bytes32 client) external;

    /**
     * @notice Sets the address of the InterchainDB contract.
     * @dev Only callable by the contract owner or an authorized account.
     * @param _interchainDB The address of the InterchainDB contract.
     */
    function setInterchainDB(address _interchainDB) external;

    /**
     * @notice Sends a message to another chain via the Interchain Communication Protocol.
     * @dev Charges a fee for the message, which is payable upon calling this function.
     * @param receiver The address of the receiver on the destination chain.
     * @param dstChainId The chain ID of the destination chain.
     * @param message The message being sent.
     * @param options Execution options for the message sent, encoded as bytes, currently primarily gas limit + native gas drop.
     * @param srcModules The source modules involved in the message sending.
     */
    function interchainSend(
        bytes32 receiver,
        uint256 dstChainId,
        bytes calldata message,
        bytes calldata options,
        address[] calldata srcModules
    )
        external
        payable;

    /**
     * @notice Executes a transaction that has been sent via the Interchain.
     * @dev The transaction must have been previously sent and recorded.
     * @param transaction The transaction data.
     */
    function interchainExecute(bytes calldata transaction) external;

    /**
     * @notice Checks if a transaction is executable.
     * @dev Determines if a transaction meets the criteria to be executed based on:
     * - If approved modules have written to the InterchainDB
     * - If the threshold of approved modules have been met
     * - If the optimistic window has passed for all modules
     * @param transaction The InterchainTransaction struct to be checked.
     * @return bool Returns true if the transaction is executable, false otherwise.
     */
    function isExecutable(bytes calldata transaction) external view returns (bool);
}

// contracts/libs/Options.sol

/// @title OptionsLib
/// @notice A library for encoding and decoding Interchain options related to interchain messages.
library OptionsLib {
    /// @dev Struct to hold V1 of options data.
    /// @param version The version of the options.
    /// @param gasLimit The gas limit for the transaction.
    /// @param gasAirdrop The amount of gas to airdrop.
    struct Options {
        uint8 version;
        uint256 gasLimit;
        // uint256 msgValue;
        uint256 gasAirdrop;
    }

    /// @notice Encodes options into a bytes format.
    /// @param options The Options to encode.
    /// @return The encoded options as bytes.
    function encodeOptions(Options memory options) internal pure returns (bytes memory) {
        return abi.encode(options.version, options.gasLimit, options.gasAirdrop);
    }

    /// @notice Decodes options from a bytes format back into an Options struct.
    /// @param data The options data in bytes format.
    /// @return The decoded options as an Options struct.
    function decodeOptions(bytes memory data) internal pure returns (Options memory) {
        (uint8 version, uint256 gasLimit, uint256 gasAirdrop) = abi.decode(data, (uint8, uint256, uint256));
        return Options(version, gasLimit, gasAirdrop);
    }
}

// contracts/libs/TypeCasts.sol

library TypeCasts {
    function addressToBytes32(address addr) internal pure returns (bytes32) {
        return bytes32(uint256(uint160(addr)));
    }

    function bytes32ToAddress(bytes32 b) internal pure returns (address) {
        return address(uint160(uint256(b)));
    }
}

