// contracts/interfaces/IInterchainDB.sol

interface IInterchainDB {
    /// @notice Struct representing an entry from the remote Interchain DataBase verified by the Interchain Module
    /// @param verifiedAt   The block timestamp at which the entry was verified by the module
    /// @param dataHash     The hash of the data written on the source chain
    struct RemoteEntry {
        uint256 verifiedAt;
        bytes32 dataHash;
    }

    error InterchainDB__ConflictingEntries(bytes32 existingDataHash, InterchainEntry newEntry);
    error InterchainDB__EntryDoesNotExist(address writer, uint256 writerNonce);
    error InterchainDB__IncorrectFeeAmount(uint256 actualFee, uint256 expectedFee);
    error InterchainDB__NoModulesSpecified();
    error InterchainDB__SameChainId();

    /// @notice Write data to the Interchain DataBase as a new entry.
    /// Note: there are no guarantees that this entry will be available for reading on any of the remote chains.
    /// Use `verifyEntry` to ensure that the entry is available for reading on the destination chain.
    /// @param dataHash     The hash of the data to be written to the Interchain DataBase as a new entry
    /// @return writerNonce The writer-specific nonce of the written entry
    function writeEntry(bytes32 dataHash) external returns (uint256 writerNonce);

    /// @notice Request the given Interchain Modules to verify the already written entry on the destination chain.
    /// Note: every module has a separate fee paid in the native gas token of the source chain,
    /// and `msg.value` must be equal to the sum of all fees.
    /// Note: this method is permissionless, and anyone can request verification for any entry.
    /// @dev Will revert if the entry with the given nonce does not exist.
    /// @param destChainId   The chain id of the destination chain
    /// @param writer        The address of the writer on the source chain
    /// @param writerNonce   The nonce of the writer on the source chain
    /// @param srcModules    The source chain addresses of the Interchain Modules to use for verification
    function requestVerification(
        uint256 destChainId,
        address writer,
        uint256 writerNonce,
        address[] memory srcModules
    )
        external
        payable;

    /// @notice Write data to the Interchain DataBase,
    /// and request the given Interchain Modules to verify it on the destination chain.
    /// Note: every module has a separate fee paid in the native gas token of the source chain,
    /// and `msg.value` must be equal to the sum of all fees.
    /// Note: additional verification for the same entry could be later done using `requestVerification`.
    /// @dev Will revert if the empty array of modules is provided.
    /// @param destChainId  The chain id of the destination chain
    /// @param dataHash     The hash of the data to be written to the Interchain DataBase as a new entry
    /// @param srcModules   The source chain addresses of the Interchain Modules to use for verification
    /// @return writerNonce The writer-specific nonce of the written entry
    function writeEntryWithVerification(
        uint256 destChainId,
        bytes32 dataHash,
        address[] memory srcModules
    )
        external
        payable
        returns (uint256 writerNonce);

    /// @notice Allows the Interchain Module to verify the entry coming from a remote source chain.
    /// @param entry        The Interchain Entry to confirm
    function verifyEntry(InterchainEntry memory entry) external;

    /// @notice Get the fee for writing data to the Interchain DataBase, and verifying it on the destination chain
    /// using the provided Interchain Modules.
    /// @dev Will revert if the empty array of modules is provided.
    /// @param destChainId  The chain id of the destination chain
    /// @param srcModules   The source chain addresses of the Interchain Modules to use for verification
    function getInterchainFee(uint256 destChainId, address[] memory srcModules) external view returns (uint256);

    /// @notice Get the Interchain Entry by the writer and the writer nonce.
    /// @dev Will revert if the entry with the given nonce does not exist.
    /// @param writer       The address of the writer on this chain
    /// @param writerNonce  The nonce of the writer's entry on this chain
    function getEntry(address writer, uint256 writerNonce) external view returns (InterchainEntry memory);

    /// @notice Get the nonce of the writer on this chain.
    /// @param writer       The address of the writer on this chain
    function getWriterNonce(address writer) external view returns (uint256);

    /// @notice Read the data written on specific source chain by a specific writer,
    /// and verify it on the destination chain using the provided Interchain Module.
    /// Note: returned zero value indicates that the module has not verified the entry.
    /// @param entry        The Interchain Entry to read
    /// @param dstModule    The destination chain addresses of the Interchain Modules to use for verification
    /// @return moduleVerifiedAt   The block timestamp at which the entry was verified by the module,
    ///                             or zero if the module has not verified the entry.
    function readEntry(
        address dstModule,
        InterchainEntry memory entry
    )
        external
        view
        returns (uint256 moduleVerifiedAt);
}

// contracts/InterchainClientV1.sol

/**
 * @title InterchainClientV1
 * @dev Implements the operations of the Interchain Execution Layer.
 */
contract InterchainClientV1 is Ownable, IInterchainClientV1 {
    uint64 public clientNonce;
    address public interchainDB;

    // Chain ID => Bytes32 Address of src clients
    mapping(uint256 => bytes32) linkedClients;

    // TODO: Add permissioning
    // @inheritdoc IInterchainClientV1
    function setLinkedClient(uint256 chainId, bytes32 client) public {
        linkedClients[chainId] = client;
    }

    constructor() Ownable(msg.sender) {}

    // @inheritdoc IInterchainClientV1
    function setInterchainDB(address _interchainDB) public onlyOwner {
        interchainDB = _interchainDB;
    }

    /**
     * @notice Emitted when an interchain transaction is sent.
     */
    event InterchainTransactionSent(
        bytes32 srcSender,
        uint256 srcChainId,
        bytes32 indexed dstReceiver,
        uint256 indexed dstChainId,
        bytes message,
        uint64 nonce,
        bytes32 indexed transactionId,
        uint256 dbWriterNonce
    );

    /**
     * @dev Represents an interchain transaction.
     */
    struct InterchainTransaction {
        bytes32 srcSender;
        uint256 srcChainId;
        bytes32 dstReceiver;
        uint256 dstChainId;
        bytes message;
        uint64 nonce;
        bytes32 transactionId;
        uint256 dbWriterNonce;
    }

    // TODO: Calculate Gas Pricing per module and charge fees
    // TODO: Customizable Gas Limit for Execution
    // @inheritdoc IInterchainClientV1
    function interchainSend(
        bytes32 receiver,
        uint256 dstChainId,
        bytes calldata message,
        address[] calldata srcModules
    )
        public
        payable
    {
        uint256 totalModuleFees = msg.value;
        bytes32 sender = convertAddressToBytes32(msg.sender);
        bytes32 transactionID = keccak256(abi.encode(sender, block.chainid, receiver, dstChainId, message, clientNonce));

        uint256 dbWriterNonce = IInterchainDB(interchainDB).writeEntryWithVerification{value: totalModuleFees}(
            dstChainId, transactionID, srcModules
        );

        emit InterchainTransactionSent(
            sender, block.chainid, receiver, dstChainId, message, clientNonce, transactionID, dbWriterNonce
        );
        // Increment nonce for next message
        clientNonce++;
    }

    function isExecutable(bytes calldata transaction) public view returns (bool) {
        InterchainTransaction memory icTx = abi.decode(transaction, (InterchainTransaction));

        // Construct expected entry based on icTransaction data
        InterchainEntry memory icEntry = InterchainEntry({
            srcChainId: icTx.srcChainId,
            srcWriter: linkedClients[icTx.srcChainId],
            writerNonce: icTx.dbWriterNonce,
            dataHash: icTx.transactionId
        });

        bytes memory reconstructedID =
            abi.encode(icTx.srcSender, icTx.srcChainId, icTx.dstReceiver, icTx.dstChainId, icTx.message, icTx.nonce);

        if (icTx.transactionId == keccak256(reconstructedID)) {
            return false;
        }

        address receivingApp = convertBytes32ToAddress(icTx.dstReceiver);

        address[] memory approvedDstModules = IInterchainApp(receivingApp).getReceivingModules();

        uint256 appRequiredResponses = IInterchainApp(receivingApp).getRequiredResponses();

        uint256 optimisticTimePeriod = IInterchainApp(receivingApp).getOptimisticTimePeriod();

    uint256[] memory moduleResponseTimestamps = new uint256[](
        approvedDstModules.length
        );

        for (uint256 i = 0; i < approvedDstModules.length; i++) {
            moduleResponseTimestamps[i] = IInterchainDB(interchainDB).readEntry(approvedDstModules[i], icEntry);
        }
        // 6. Confirm module threshold is met
        uint256 validResponses = 0;

        for (uint256 i = 0; i < moduleResponseTimestamps.length; i++) {
            if (moduleResponseTimestamps[i] + optimisticTimePeriod >= block.timestamp) {
                validResponses++;
            }
        }

        if (validResponses >= appRequiredResponses) {
            return true;
        } else {
            return false;
        }
    }

    // function _getValidResponses(address[] memory approvedModules, InterchainEntry memory icEntry) internal view returns (uint256) {
    //     uint256 validResponses = 0;
    //     for (uint256 i = 0; i < approvedModules.length; i++) {
    //         uint256 moduleResponseTimestamp = IInterchainDB(interchainDB).readEntry(approvedModules[i], icEntry);
    //         if (moduleResponseTimestamp + optimisticTimePeriod >= block.timestamp) {
    //             validResponses++;
    //         }
    //     }
    //     return validResponses;
    // }

    // TODO: Gas Fee Consideration that is paid to executor
    // @inheritdoc IInterchainClientV1
    function interchainExecute(bytes32 transactionID, bytes calldata transaction) public {
        // Steps to verify:
        // 1. Call icDB.getEntry(linkedClients.srcChainId, transaction.dbWriterNonce)
        // 2. Verify the entry hash vs bytes calldata provided
        // 3. Check receiver's app dstModule configuration
        // 4. Check receiver app's optimistic time period
        // 5. Read module entry's based on receiver app dstModule config
        // 6. Confirm module threshold is met
        // 7. Check optimistic threshold set on app config
        // 8. Execute the transaction, is optimistic period is met.

        InterchainTransaction memory icTx = abi.decode(transaction, (InterchainTransaction));

        // 1. Call icDB.getEntry(linkedClients.srcChainId, transaction.dbWriterNonce)
        InterchainEntry memory icEntry = InterchainEntry({
            srcChainId: icTx.srcChainId,
            srcWriter: linkedClients[icTx.srcChainId],
            writerNonce: icTx.dbWriterNonce,
            dataHash: icTx.transactionId
        });

        bytes memory reconstructedID =
            abi.encode(icTx.srcSender, icTx.srcChainId, icTx.dstReceiver, icTx.dstChainId, icTx.message, icTx.nonce);

        // 2. Verify the provided TX ID == constructed TX data
        require(icTx.transactionId == keccak256(reconstructedID), "Invalid transaction ID");

        address receivingApp = convertBytes32ToAddress(icTx.dstReceiver);
        // 3. Check receiver's app dstModule configuration
        address[] memory approvedDstModules = IInterchainApp(receivingApp).getReceivingModules();

        uint256 appRequiredResponses = IInterchainApp(receivingApp).getRequiredResponses();

        // 4. Check receiver app's optimistic time period
        uint256 optimisticTimePeriod = IInterchainApp(receivingApp).getOptimisticTimePeriod();

        // 5. Read module entry's based on receiver app dstModule config
        uint256[] memory moduleResponseTimestamps = new uint256[](
      approvedDstModules.length
    );

        for (uint256 i = 0; i < approvedDstModules.length; i++) {
            moduleResponseTimestamps[i] = IInterchainDB(interchainDB).readEntry(approvedDstModules[i], icEntry);
        }
        // 6. Confirm module threshold is met
        uint256 validResponses = 0;

        for (uint256 i = 0; i < moduleResponseTimestamps.length; i++) {
            if (moduleResponseTimestamps[i] + optimisticTimePeriod >= block.timestamp) {
                validResponses++;
            }
        }

        require(validResponses >= appRequiredResponses, "Not enough valid responses to meet the threshold");

        // 8. Execute the transaction, is optimistic period & valid responses is met.
        IInterchainApp(receivingApp).appReceive();
    }

    // TODO: Seperate out into utils
    /**
     * @inheritdoc IInterchainClientV1
     */
    function convertBytes32ToAddress(bytes32 _bytes32) public pure returns (address) {
        return address(uint160(uint256(_bytes32)));
    }

    /**
     * @inheritdoc IInterchainClientV1
     */
    function convertAddressToBytes32(address _address) public pure returns (bytes32) {
        return bytes32(uint256(uint160(_address)));
    }
}

